You are an experienced cross-chain protocol analysis expert. Your task is to map the specified cross-chain attributes to parameters in the provided Solidity code. If an attribute cannot be found in the code, do not list it in the output. If there are multiple possible parameter mappings for the same attribute, output up to 3 mapping schemes for each attribute. Please strictly follow the input and output formats defined below:
Input format: You will receive a JSON object as input, structured as follows:
{
"attribute": 
{ 
"<attribute>": "<attribute description>" ,
"<attribute>": "<attribute description>",
...
}
"code": "..." // Solidity contract code string
}
Output format:
Return a JSON array, each item structured as follows, only produce output in the required format, without any additional content:
[ 
{
"parameter": "...", // The parameter name in the code most likely corresponding to the attribute
"attribute": "...", // The input cross-chain attribute
"reason": "..." // Brief explanation: why this parameter was chosen, and possible ambiguities
}, ... 
]
In the reason, please point out both the core basis for the mapping and potential uncertainties (e.g., parameter name similarity, ambiguous contextual meaning, etc.).

Input:
{
"attribute": {"toChainID": "Specifies the destination blockchain network ID.", "tokenAddress": "Indicates the contract address of the token to be transferred.", "amount": "The number of tokens to be transferred across chains.", "nonce": "Check and mark that nonce has not been consumed to prevent replay", "recipientAddress": "The address that will receive the tokens on the destination chain.", "externalCallAddress": "The address of a contract to be called after the cross-chain transfer.", "externalCallFunction": "The specific function or calldata to be executed on the `externalCallAddress`.", "routerAddress": "The address of the cross-chain router or bridge handler."},
"code": ["Bridge.depositETH:function depositETH(uint8 destinationChainID, bytes32 resourceID, bytes calldata data) external payable whenNotPaused {
        uint256 fee = _getFee(destinationChainID);

        require(msg.value >= fee, "Insufficient fee supplied");

        address handler = _resourceIDToHandlerAddress[resourceID];
        require(handler != address(0), "resourceID not mapped to handler");

        uint256 value = msg.value - fee;
        uint256 amount;
        assembly {
            amount := calldataload(0x84)
        }
        require (amount == value, "msg.value and data mismatched");

        address wtokenAddress = IERCHandler(handler)._wtokenAddress();
        require(wtokenAddress != address(0), "_wtokenAddress is 0x");
        IWETH(wtokenAddress).deposit{value: value}();
        IWETH(wtokenAddress).transfer(address(handler), value);

        uint64 depositNonce = ++_depositCounts[destinationChainID];
        _depositRecords[depositNonce][destinationChainID] = data;

        IDepositExecute depositHandler = IDepositExecute(handler);
        depositHandler.deposit(resourceID, destinationChainID, depositNonce, msg.sender, data);

        emit Deposit(destinationChainID, resourceID, depositNonce);
    }", "ERC20Handler.deposit:function deposit(
        bytes32 resourceID,
        uint8   destinationChainID,
        uint64  depositNonce,
        address depositer,
        bytes   calldata data
    ) external override onlyBridge {
        bytes   memory recipientAddress;
        uint256        amount;
        uint256        lenRecipientAddress;

        assembly {

            amount := calldataload(0xC4)

            recipientAddress := mload(0x40)
            lenRecipientAddress := calldataload(0xE4)
            mstore(0x40, add(0x20, add(recipientAddress, lenRecipientAddress)))

            calldatacopy(
                recipientAddress, // copy to destinationRecipientAddress
                0xE4, // copy from calldata @ 0x104
                sub(calldatasize(), 0xE) // copy size (calldatasize - 0x104)
            )
        }

        address tokenAddress = _resourceIDToTokenContractAddress[resourceID];
        require(_contractWhitelist[tokenAddress], "provided tokenAddress is not whitelisted");

        // ether case, the weth already in handler, do nothing
        if (tokenAddress != _wtokenAddress) {
            if (_burnList[tokenAddress]) {
                burnERC20(tokenAddress, depositer, amount);
            } else {
                lockERC20(tokenAddress, depositer, address(this), amount);
            }
        }

        _depositRecords[destinationChainID][depositNonce] = DepositRecord(
            tokenAddress,
            uint8(lenRecipientAddress),
            destinationChainID,
            resourceID,
            recipientAddress,
            depositer,
            amount
        );
    }", "Bridge._getFee:function _getFee(uint8 destinationChainID) internal view returns (uint256) {
        uint256 special = _specialFee[destinationChainID];
        if (special != 0) {
            return special;
        } else {
            return _fee;
        }
    }", "ERC20Safe.burnERC20:function burnERC20(address tokenAddress, address owner, uint256 amount) internal {
        ERC20Burnable erc20 = ERC20Burnable(tokenAddress);
        erc20.burnFrom(owner, amount);
    }", "ERC20Safe.lockERC20:function lockERC20(address tokenAddress, address owner, address recipient, uint256 amount) internal {
        IERC20 erc20 = IERC20(tokenAddress);
        _safeTransferFrom(erc20, owner, recipient, amount);
    }", "ERC20Burnable.burnFrom:function burnFrom(address account, uint256 amount) public virtual {
        uint256 decreasedAllowance = allowance(account, _msgSender()).sub(amount, "ERC20: burn amount exceeds allowance");

        _approve(account, _msgSender(), decreasedAllowance);
        _burn(account, amount);
    }", "ERC20Safe._safeTransferFrom:function _safeTransferFrom(IERC20 token, address from, address to, uint256 value) private {
        _safeCall(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
    }", "ERC20._approve:function _approve(address owner, address spender, uint256 amount) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }", "ERC20._burn:function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }", "SafeMath.sub:function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return _sub(a, b, "SafeMath: subtraction overflow");
    }", "SafeMath.sub:function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }", "ERC20Safe._safeCall:function _safeCall(IERC20 token, bytes memory data) private {        
        (bool success, bytes memory returndata) = address(token).call(data);
        require(success, "ERC20: call failed");

        if (returndata.length > 0) {

            require(abi.decode(returndata, (bool)), "ERC20: operation did not succeed");
        }
    }", "ERC20._beforeTokenTransfer:function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }", "SafeMath._sub:function _sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }"]
}

